package tutorials.jenkov.com.variables;

public class Variables2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 0;
		int abs = 0;
		if (number >= 0) {
			abs = number;
			System.out.println(" number : " + number );
		} else {
			abs = -number;
			System.out.println(" -number : " + -number );
		}

	}

}
